import React,{useState,useEffect} from 'react';
import {useParams} from 'react-router-dom';
import { Redirect } from 'react-router';

const EditPost = function(props){
    const [posts, setPosts] = useState([]);
    
    const {id} = useParams();
    const newId = {id};
    const postId = newId.id.replace(':','');
    //console.log(postId);

    const [value, setValue] = useState({
        postTitle:'',
        postContent:''
    });

    const [submitted, setSubmitted] = useState(false);
    const onChangeHandler = (e) =>{
        // console.log(e.target.value)
        // e.target.name = e.target.value;
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    }
    const onSubmitHandler = (e) =>{
        e.preventDefault();
       
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                title: value.postTitle,
                content: value.postContent
            })
        }
        fetch("http://localhost:3000/posts",requestOptions)
        .then((res)=>{
            setSubmitted(true);
            res.json().then((data)=>{
                return setPosts(data);
            })
        })
        
      }
      if (submitted) {
        return <Redirect push to={{
          pathname: '/'
        }}
        />
      } 
    
    const apiURL = 'http://localhost:3000/posts?id='+postId;
    //console.log(apiURL);
    async function fetchData() {
        const res = await fetch(apiURL);
        const data = await res.json();
        console.log(data);
        return setPosts(data);          
      }
    useEffect(() => {
    fetchData();        
    },[]);

    return <>   
      
        {
            
            posts.map((item) =>{
                return <>
                <h2 className="mb-5">Add Blog Post Here</h2>
                    <form onSubmit = {onSubmitHandler}>
                        <div className="form-group mb-4">
                            <labe>Blog title</labe>
                            <input type="text" name="postTitle" class="form-control" required onChange = {onChangeHandler}/>
                        </div>
                        <div className="form-group mb-4">
                            <labe>Blog content</labe>
                            <textarea name="postContent" class="form-control" required onChange = {onChangeHandler}></textarea>
                        </div>
                        <p className="text-end"><button className="btn btn-info">Submit Post</button></p>
                    </form>
                </>
            })
        }
       
    </>
}

export default EditPost;
