import React,{useState, useEffect} from 'react';

const Home = function(){
    const [posts, setPosts] = useState("");
    console.log('before posts' + posts);
    useEffect(() =>{
        fetch('http://localhost:3000/posts')
    .then((response) =>{
        response.json().then((result) =>{            
            console.log(posts);
            setPosts(()=>{
                posts.push(result);
            });
            console.log('posts' + posts);
        });
    })
    },[])

    return <>
        <h1>Blogs</h1>
        
    </>
}

export default Home
