import React,{useState,useEffect} from 'react';
import {useParams} from 'react-router-dom';

const EditPost = function(props){
    const [posts, setPosts] = useState([]);
    
    const {id} = useParams();
    const newId = {id};
    const postId = newId.id.replace(':','');
    //console.log(postId);
    
    const apiURL = 'http://localhost:3000/posts?id='+postId;
    //console.log(apiURL);
    async function fetchData() {
        const res = await fetch(apiURL);
        const data = await res.json();
        console.log(data);
        return setPosts(data);          
      }
      useEffect(() => {
        fetchData();        
      },[]);

    return <>   
    <div>{posts.title}</div>    
        {
            
            posts.map((item) =>{
                return (<article className="card mb-4" key={item.id}>
                <div className="card-header">
                <h2 className="h4">{item.title}</h2>
                </div>                    
                <div className="card-body article-content">
                    {item.content}
                </div>
            </article>)
            })
        }
       
    </>
}

export default EditPost;
