import React,{useState,useEffect} from 'react';

const PostDetail = function(props){
    const [posts, setPosts] = useState([]);
    console.log('before posts' + posts);
    async function fetchData() {
        const res = await fetch("http://localhost:3000/posts?id=2");
        const data = await res.json();

        return setPosts(data);
          
      }
      useEffect(() => {
        fetchData();
        console.log('after posts' + posts);
      },[]);

    return <>       
        {
            posts.map((item) =>{
                return (<article className="card mb-4" key={item.id}>
                <div className="card-header">
                <h2 className="h4">{item.title}</h2>
                </div>                    
                <div className="card-body article-content">
                    {item.content}
                </div>
            </article>)
            })
        }
       
    </>
}

export default PostDetail;
