import React,{useState} from 'react';


const Login = () => {
    const [value, setValue] = useState({
        username:'',
        password:''
    });

    const onChangeHandler = (e) =>{
        
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    }

    const onSubmitHandler = (e) =>{
        e.preventDefault();  
        console.log(`submitted with the values ${value.username} and ${value.password}`);
        axios.get('http://localhost:3000/login')
        .then(res => {
          const pdata = res.data;
          //return setPosts(pdata);
          console.log(pdata);
        })    
      }
    return <>
        <div className="row justify-content-center">
            <div className="col-md-6">
                <div className="card">
                    <h5 className="card-header">Login</h5>
                    <div className="card-body">
                        <form onSubmit={onSubmitHandler}>
                            <div className="mb-3">
                                <label for="username" className="form-label">Username</label>
                                <input type="text" className="form-control" id="username" aria-describedby="usernameHelp" name="username" onChange = {onChangeHandler} />
                                
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputPassword1" className="form-label">Password</label>
                                <input type="password" className="form-control" id="exampleInputPassword1"  name="password" onChange = {onChangeHandler} />
                            </div>

                            <button type="submit" className="btn btn-primary">Submit</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </>
}

export default Login
