import React,{useState} from 'react';

const AddPost = function(){
    const [posts, setPosts] = useState([]);
    const [value, setValue] = useState({
        postTitle:'',
        postContent:''
    });

    
    const onChangeHandler = (e) =>{
        // console.log(e.target.value)
        // e.target.name = e.target.value;
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    }
      const onSubmitHandler = (e) =>{
        e.preventDefault();
       
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                title: value.postTitle,
                content: value.postContent
            })
        }
        fetch("http://localhost:3000/posts",requestOptions)
        .then((res)=>{
            res.json().then((data)=>{
                return setPosts(data);
            })
        })
        
      }
    return <>
        <h2 className="mb-5">Add Blog Post Here</h2>
        <form onSubmit = {onSubmitHandler}>
            <div className="form-group mb-4">
                <labe>Blog title</labe>
                <input type="text" name="postTitle" class="form-control" required onChange = {onChangeHandler}/>
            </div>
            <div className="form-group mb-4">
                <labe>Blog content</labe>
                <textarea name="postContent" class="form-control" required onChange = {onChangeHandler}></textarea>
            </div>
            <p className="text-end"><button className="btn btn-info">Submit Post</button></p>
        </form>
    </>
}

export default AddPost
