import React,{useState,useEffect} from 'react';

const AddPost = function(){
    const [posts, setPosts] = useState([]);
    const requestOptions = {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            title: 'dummy title coming here',
            content: 'dummy content will come here.'
        })
    }
    async function fetchData() {
        const res = await fetch("http://localhost:3000/posts",requestOptions);
        const data = await res.json();

        return setPosts(data);
          
      }
      useEffect(() => {
        fetchData();       
      },[]);
      const onSubmitHandler = (e) =>{
        e.preventDefault();
      }
    return <>
        <h2 className="mb-5">Add Blog Post Here</h2>
        <form onSubmit = {onSubmitHandler}>
            <div className="form-group mb-4">
                <labe>Blog title</labe>
                <input type="text" name="postTitle" class="form-control" required/>
            </div>
            <div className="form-group mb-4">
                <labe>Blog content</labe>
                <textarea name="postContent" class="form-control" required></textarea>
            </div>
            <p className="text-end"><button className="btn btn-info">Submit Post</button></p>
        </form>
    </>
}

export default AddPost
